import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  projectTitle = "Learn Angular 16";
  mycolor = "green";
  status = false;
  showData(){
    alert("Button Clicked")
    this.status=true
  };
}

<div class="main">
   <div class="toolbar">
    <span><h1>Welcome</h1></span>
   </div>

    Name of Project: <font [color]="mycolor">{{projectTitle}}</font><br>
    <button :[disabled]="status">Click Me</button>
    <button :(click)=showData()>Click Me</button>
</div>
