import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  projectTitle = "Learn Angular 16";
  mycolor = "green";
  status = false;
  name="angular";

  Books=[
    {"BookName":"Harry Potter", "BookAuthor":"J K Rowling"},
    {"BookName":"Myself", "BookAuthor":"Atharva Shengule"},
    {"BookName":" SIH Trip to Bhopal", "BookAuthor":"Vedshree kulkarni"},
  ]
  showData(){
    alert("Button Clicked")
    this.status=true
  };
}

<div class="main">
   <div class="toolbar">
    <span><h1>Welcome</h1></span>
   </div>

    Name of Project: <font [color]="mycolor">{{projectTitle}}</font><br>
    <input type="text" [(ngModel)]="name">{{name}}<br>
    <button :[disabled]="status">Click Me</button><br>
    <button :(click)=showData()>Click Me</button><br>
</div>
<app-detail>This is Detail About Webpage</app-detail>
<p>List of Books</p>
<div *ngFor="let book of Books"></div>
<h3>{{Books.BookName}},{{Books.BookAuthor}}</h3>
<app-form-handling></app-form-handling>
